import importlibfrom flask import Flask, request, Responsefrom asgiref.wsgi import WsgiToAsgifrom typing import Unionapp_flask = Flask(__name__)def get_result_after_invoke(module_path: str, http_request: request) -> Union[Response, str]:    module = importlib.import_module(module_path)    fn_invoke = getattr(module, "invoke")    return fn_invoke(http_request)@app_flask.route("/api/v1/etl/mysql", methods=["GET"])async def run_etl_mysql() -> Response:    return get_result_after_invoke(        "etl.infrastructure.controllers.etl_mysql_controller",        request    )@app_flask.route("/api/v1/health-check", methods=["GET"])async def get_health_check_controller() -> Response:    return get_result_after_invoke(        "health_check.infrastructure.controllers.get_health_check_controller",        request    )@app_flask.route("/", methods=["GET"])async def get_documentation_controller():    return get_result_after_invoke(        "api_doc.infrastructure.controllers.get_documentation_controller",        request    )if __name__ == "__main__":    import uvicorn    async_flask = WsgiToAsgi(app_flask)    uvicorn.run(async_flask, host="127.0.0.1", port=5000, log_level="error")