from flask import Flask, request, Responsefrom modules.shared.infrastructure.components.flask_module_importer import get_result_after_invokeapp_flask = Flask(__name__)@app_flask.route("/api/v1/whatsapp/send-text-message", methods=["POST"])async def whatsapp_send_text_message() -> Response:    return get_result_after_invoke(         "whatsapp.infrastructure.controllers.send_message_controller",        request    )@app_flask.route("/api/v1/chat-gpt/pdf-question", methods=["GET"])async def chat_gpt_pdf_question_controller() -> Response:    return get_result_after_invoke(         "modules.open_ai.infrastructure.controllers.talk_to_gpt35_controller",        request    )@app_flask.route("/api/v1/chat-gpt/ask", methods=["GET"])async def chat_gpt_ask_question() -> Response:    return get_result_after_invoke(         "modules.open_ai.infrastructure.controllers.talk_to_gpt35_controller",        request    )@app_flask.route("/api/v1/health-check", methods=["GET"])async def get_health_check_controller() -> Response:    return get_result_after_invoke(        "modules.health_check.infrastructure.controllers.get_health_check_controller",        request    )@app_flask.route("/", methods=["GET"])async def get_documentation_controller():    return get_result_after_invoke(        "modules.api_doc.infrastructure.controllers.get_documentation_controller",        request    )if __name__ == "__main__":    app_flask.run()