from flask import Flask, request, Responsefrom modules.shared.infrastructure.components.flask_module_importer import get_result_after_invokefrom modules.shared.infrastructure.enums.http_route_enum import HttpRouteEnumfrom modules.shared.infrastructure.enums.module_path_enum import ModulePathEnumapp_flask = Flask(__name__)@app_flask.route(HttpRouteEnum.WHATSAPP_SEND_TEXT_MESSAGE.value, methods=["POST"])async def whatsapp_send_text_message() -> Response:    return get_result_after_invoke(        ModulePathEnum.WHATSAPP_SEND_TEXT_MESSAGE.value,        request    )@app_flask.route(HttpRouteEnum.CHAT_GPT_PDF_QUESTION.value, methods=["GET"])async def chat_gpt_pdf_question_controller() -> Response:    return get_result_after_invoke(        ModulePathEnum.CHAT_GPT_PDF_QUESTION.value,        request    )@app_flask.route(HttpRouteEnum.CHAT_GPT_ASK_QUESTION.value, methods=["GET"])async def chat_gpt_ask_question() -> Response:    return get_result_after_invoke(        ModulePathEnum.CHAT_GPT_ASK_QUESTION.value,        request    )@app_flask.route(HttpRouteEnum.HEALTH_CHECK.value, methods=["GET"])async def get_health_check_controller() -> Response:    return get_result_after_invoke(        ModulePathEnum.HEALTH_CHECK.value,        request    )@app_flask.route(HttpRouteEnum.DOCUMENTATION.value, methods=["GET"])async def get_documentation_controller() -> Response:    return get_result_after_invoke(        ModulePathEnum.DOCUMENTATION.value,        request    )if __name__ == "__main__":    app_flask.run()