from flask import Flask, request, Responsefrom modules.shared.infrastructure.components.flask_module_importer import get_result_after_invokefrom modules.shared.infrastructure.enums.http_route_enum import HttpRouteEnumfrom modules.shared.infrastructure.enums.module_path_enum import ModulePathEnumapp_flask = Flask(__name__)@app_flask.route(HttpRouteEnum.WHATSAPP_SEND_TEXT_MESSAGE.value, methods=["POST"])async def exec_controller() -> Response:    return get_result_after_invoke(        ModulePathEnum.WHATSAPP_SEND_TEXT_MESSAGE.value,        request    )@app_flask.route(HttpRouteEnum.CHAT_GPT_PDF_QUESTION.value, methods=["GET"])async def exec_controller1() -> Response:    return get_result_after_invoke(        ModulePathEnum.CHAT_GPT_PDF_QUESTION.value,        request    )@app_flask.route(HttpRouteEnum.CHAT_GPT_ASK_QUESTION.value, methods=["GET"])async def exec_controller2() -> Response:    return get_result_after_invoke(        ModulePathEnum.CHAT_GPT_ASK_QUESTION.value,        request    )@app_flask.route(HttpRouteEnum.HEALTH_CHECK.value, methods=["GET"])async def exec_controller3() -> Response:    return get_result_after_invoke(        ModulePathEnum.HEALTH_CHECK.value,        request    )@app_flask.route(HttpRouteEnum.DOCUMENTATION.value, methods=["GET"])async def exec_controller4() -> Response:    return get_result_after_invoke(        ModulePathEnum.DOCUMENTATION.value,        request    )if __name__ == "__main__":    app_flask.run()